 <?php
    if (isset($_POST["generate_seller_report"]) && isset($_POST["sellerName"]) && isset($_POST["fromDate"]) && isset($_POST["toDate"])) {
        require_once 'pdf.php';
        include('invoice-db-connection.php');
        $output = '';

        $commissionRateQuery = $connect->prepare("
SELECT CommissionRate from sellers where sellerid=:sellerid
 ");
        $commissionRateQuery->execute(
            array(
                ':sellerid'       =>  $_POST["sellerName"],
            )
        );
        $commissionRows = $commissionRateQuery->fetchAll();

        foreach ($commissionRows as $com) {
            $commissionRate = $com["CommissionRate"] / 100;
        }


        $mainQuery = $connect->prepare("
SELECT SUM(Quantity) As Quantity, Sum(Quantity)*UnitPrice As TotalPrice, ItemDesc, UnitPrice
FROM invoiceline il inner join invoiceinfo ifi on ifi.invoicenb=il.invoicenb
where sellerid=:sellerid and ifi.InvoiceDate BETWEEN :fromDate and :toDate
GROUP BY ItemDesc , UnitPrice
 ");
        $mainQuery->execute(
            array(
                ':sellerid'       =>  $_POST["sellerName"],
                ':fromDate' => trim($_POST["fromDate"]),
                ':toDate' => trim($_POST["toDate"]),
            )
        );
        $result = $mainQuery->fetchAll();
        $output .= '
   <table width="100%" border="1" cellpadding="5" cellspacing="0">
    <tr>
     <td colspan="2" align="center" style="font-size:18px">
    
     <b>Seller Report</b>
 
     </td>
    </tr>
    <tr>
     <td colspan="2">
      <table width="100%" cellpadding="5">
       <tr>
        <td width="33.3%">
         To,<br />
         <b>SELLER</b><br />
         Name : ' . $_POST["sellerName"] . '<br />

        </td>
        <td width="33.3%">
             <img alt="" src="data:image/png;base64," />
        </td>
        <td width="33.3%">
         <br />
         From Date. : ' . $_POST["fromDate"] . '<br />
         To Date : ' . $_POST["toDate"] . '<br />
        </td>
       </tr>
      </table>
      <br />
      <table width="100%" border="1" cellpadding="5" cellspacing="0">
      
       <tr>
        <th>Quantity</th>
        <th>TotalPrice</th>
        <th>Item Desc.</th>
        <th>Unit Price</th>


       </tr>
       ';

        $count = 0;
        foreach ($result as $sub_row) {
            $count++;
            $output .= '
   <tr>
    <td>' . $sub_row["Quantity"] . '</td>
    <td>' . $sub_row["TotalPrice"] . '</td>
    <td>' . $sub_row["ItemDesc"] . '</td>
    <td>' . $sub_row["UnitPrice"] . '</td>

   </tr>

   ';
        }
        $totalQuery = $connect->prepare("
SELECT Sum(TotalPrice) As Total From (SELECT SUM(Quantity) As Quantity, Sum(Quantity)*UnitPrice As TotalPrice, ItemDesc, UnitPrice
FROM invoiceline il inner join invoiceinfo ifi on ifi.invoicenb=il.invoicenb
where sellerid=:sellerid and ifi.InvoiceDate BETWEEN :fromDate and :toDate
GROUP BY ItemDesc , UnitPrice) t
 ");
        $totalQuery->execute(
            array(
                ':sellerid'       =>  $_POST["sellerName"],
                ':fromDate' => trim($_POST["fromDate"]),
                ':toDate' => trim($_POST["toDate"]),
            )
        );
        $totalRows = $totalQuery->fetchAll();
        foreach ($totalRows as $tot) {
            $output .= '
            <p><b>Total: ' . $tot["Total"] . ' LBP</b></p>
            <br>
            <p><b>My Commission: ' . ($commissionRate * $tot["Total"]) . ' LBP</b></p>
            <p><b>Amount to pay to seller: ' . ($tot["Total"] - ($commissionRate * $tot["Total"])) . ' LBP</b></p>
            ';
        }

        $output .= '
      </table>
     </td>
    </tr>
   </table>


  ';
        $f;
        $l;
        if (headers_sent($f, $l)) {
            echo $f, '<br/>', $l, '<br/>';
            die('now detect line');
        }


        $pdf = new Pdf();
        $file_name = 'Report-' . $count . '.pdf';
        $pdf->loadHtml($output);
        $pdf->render();
        $pdf->stream($file_name, array("Attachment" => false));
    }
    ?>